package event_test

import (
	"time"

	"github.com/crypto-com/chain-indexing/external/json"
	"github.com/mitchellh/mapstructure"
	. "github.com/onsi/ginkgo"
	. "github.com/onsi/gomega"

	event_entity "github.com/crypto-com/chain-indexing/entity/event"
	"github.com/crypto-com/chain-indexing/internal/must"
	event_usecase "github.com/crypto-com/chain-indexing/usecase/event"
	ibc_model "github.com/crypto-com/chain-indexing/usecase/model/ibc"
	mapstructure_utils "github.com/crypto-com/chain-indexing/usecase/parser/utils/mapstructure"
)

var _ = Describe("Event", func() {
	registry := event_entity.NewRegistry()
	event_usecase.RegisterEvents(registry)

	Describe("En/DecodeMsgIBCConnectionOpenAck", func() {
		It("should able to encode and decode to the same event", func() {
			anyHeight := int64(1000)
			anyTxHash := "4936522F7391D425F2A93AD47576F8AEC3947DC907113BE8A2FBCFF8E9F2A416"
			anyMsgIndex := 2
			anyClientId := "tendermint-0"
			anyCounterpartyClientId := "tendermint-1"

			var anyRawValue map[string]interface{}
			var anyRawMsgConnectionOpenAck ibc_model.RawMsgConnectionOpenAckTendermintClient
			decoder, _ := mapstructure.NewDecoder(&mapstructure.DecoderConfig{
				WeaklyTypedInput: true,
				DecodeHook: mapstructure.ComposeDecodeHookFunc(
					mapstructure.StringToTimeDurationHookFunc(),
					mapstructure.StringToTimeHookFunc(time.RFC3339),
					mapstructure_utils.StringToDurationHookFunc(),
					mapstructure_utils.StringToByteSliceHookFunc(),
				),
				Result: &anyRawMsgConnectionOpenAck,
			})
			json.MustUnmarshalFromString(`
{
  "@type": "/ibc.core.connection.v1.MsgConnectionOpenAck",
  "connection_id": "connection-0",
  "counterparty_connection_id": "connection-0",
	"version": {
	  "identifier": "1",
	  "features": [
		"ORDER_ORDERED",
		"ORDER_UNORDERED"
	  ]
	},
	"client_state": {
	  "@type": "/ibc.lightclients.tendermint.v1.ClientState",
	  "chain_id": "devnet-1",
	  "trust_level": {
		"numerator": "1",
		"denominator": "3"
	  },
	  "trusting_period": "1209600s",
	  "unbonding_period": "1814400s",
	  "max_clock_drift": "5s",
	  "frozen_height": {
		"revision_number": "0",
		"revision_height": "0"
	  },
	  "latest_height": {
		"revision_number": "1",
		"revision_height": "8"
	  },
	  "proof_specs": [
		{
		  "leaf_spec": {
			"hash": "SHA256",
			"prehash_key": "NO_HASH",
			"prehash_value": "SHA256",
			"length": "VAR_PROTO",
			"prefix": "AA=="
		  },
		  "inner_spec": {
			"child_order": [
			  0,
			  1
			],
			"child_size": 33,
			"min_prefix_length": 4,
			"max_prefix_length": 12,
			"empty_child": null,
			"hash": "SHA256"
		  },
		  "max_depth": 0,
		  "min_depth": 0
		},
		{
		  "leaf_spec": {
			"hash": "SHA256",
			"prehash_key": "NO_HASH",
			"prehash_value": "SHA256",
			"length": "VAR_PROTO",
			"prefix": "AA=="
		  },
		  "inner_spec": {
			"child_order": [
			  0,
			  1
			],
			"child_size": 32,
			"min_prefix_length": 1,
			"max_prefix_length": 1,
			"empty_child": null,
			"hash": "SHA256"
		  },
		  "max_depth": 0,
		  "min_depth": 0
		}
	  ],
	  "upgrade_path": [
		"upgrade",
		"upgradedIBCState"
	  ],
	  "allow_update_after_expiry": false,
	  "allow_update_after_misbehaviour": false
	},
	"proof_height": {
	  "revision_number": "2",
	  "revision_height": "11"
	},
	"proof_try": "",
	"proof_client": "",
	"proof_consensus": "",
	"consensus_height": {
	  "revision_number": "1",
	  "revision_height": "8"
	},
	"signer": "cro10snhlvkpuc4xhq82uyg5ex2eezmmf5ed5tmqsv"
}

`, &anyRawValue)
			must.Do(decoder.Decode(anyRawValue))

			anyParams := ibc_model.MsgConnectionOpenAckParams{
				MsgConnectionOpenAckBaseParams: anyRawMsgConnectionOpenAck.MsgConnectionOpenAckBaseParams,
				MaybeTendermintClientState:     &anyRawMsgConnectionOpenAck.TendermintClientState,

				ClientID:             anyClientId,
				CounterpartyClientID: anyCounterpartyClientId,
			}

			event := event_usecase.NewMsgIBCConnectionOpenAck(event_usecase.MsgCommonParams{
				BlockHeight: anyHeight,
				TxHash:      anyTxHash,
				TxSuccess:   true,
				MsgIndex:    anyMsgIndex,
			}, anyParams)

			encoded, err := event.ToJSON()
			Expect(err).To(BeNil())

			decodedEvent, err := registry.DecodeByType(
				event_usecase.MSG_IBC_CONNECTION_OPEN_ACK_CREATED, 1, []byte(encoded),
			)
			Expect(err).To(BeNil())
			Expect(decodedEvent).To(Equal(event))
			typedEvent, _ := decodedEvent.(*event_usecase.MsgIBCConnectionOpenAck)
			Expect(typedEvent.Name()).To(Equal(event_usecase.MSG_IBC_CONNECTION_OPEN_ACK_CREATED))
			Expect(typedEvent.Version()).To(Equal(1))
			Expect(typedEvent.TxSuccess()).To(BeTrue())
			Expect(typedEvent.TxHash()).To(Equal(anyTxHash))

			Expect(typedEvent.MsgTxHash).To(Equal(anyTxHash))
			Expect(typedEvent.MsgIndex).To(Equal(anyMsgIndex))

			Expect(typedEvent.Params.ConnectionID).To(Equal(anyParams.ConnectionID))
			Expect(typedEvent.Params.CounterpartyConnectionID).To(Equal(anyParams.CounterpartyConnectionID))
			Expect(typedEvent.Params.Version).To(Equal(anyParams.Version))
			Expect(typedEvent.Params.ProofHeight).To(Equal(anyParams.ProofHeight))
			Expect(typedEvent.Params.ProofTry).To(Equal(anyParams.ProofTry))
			Expect(typedEvent.Params.ProofClient).To(Equal(anyParams.ProofClient))
			Expect(typedEvent.Params.ProofConsensus).To(Equal(anyParams.ProofConsensus))
			Expect(typedEvent.Params.ConsensusHeight).To(Equal(anyParams.ConsensusHeight))
			Expect(typedEvent.Params.Signer).To(Equal(anyParams.Signer))

			Expect(typedEvent.Params.MaybeTendermintClientState).To(Equal(anyParams.MaybeTendermintClientState))

			Expect(typedEvent.Params.ClientID).To(Equal(anyParams.ClientID))
			Expect(typedEvent.Params.CounterpartyClientID).To(Equal(anyParams.CounterpartyClientID))
		})

		It("should able to encode and decode failed event", func() {
			anyHeight := int64(1000)
			anyTxHash := "4936522F7391D425F2A93AD47576F8AEC3947DC907113BE8A2FBCFF8E9F2A416"
			anyMsgIndex := 2
			anyClientId := "tendermint-0"
			anyCounterpartyClientId := "tendermint-1"

			var anyRawValue map[string]interface{}
			var anyRawMsgConnectionOpenAck ibc_model.RawMsgConnectionOpenAckTendermintClient
			decoder, _ := mapstructure.NewDecoder(&mapstructure.DecoderConfig{
				WeaklyTypedInput: true,
				DecodeHook: mapstructure.ComposeDecodeHookFunc(
					mapstructure.StringToTimeDurationHookFunc(),
					mapstructure.StringToTimeHookFunc(time.RFC3339),
					mapstructure_utils.StringToDurationHookFunc(),
					mapstructure_utils.StringToByteSliceHookFunc(),
				),
				Result: &anyRawMsgConnectionOpenAck,
			})
			json.MustUnmarshalFromString(`
{
  "@type": "/ibc.core.connection.v1.MsgConnectionOpenAck",
  "connection_id": "connection-0",
  "counterparty_connection_id": "connection-0",
	"version": {
	  "identifier": "1",
	  "features": [
		"ORDER_ORDERED",
		"ORDER_UNORDERED"
	  ]
	},
	"client_state": {
	  "@type": "/ibc.lightclients.tendermint.v1.ClientState",
	  "chain_id": "devnet-1",
	  "trust_level": {
		"numerator": "1",
		"denominator": "3"
	  },
	  "trusting_period": "1209600s",
	  "unbonding_period": "1814400s",
	  "max_clock_drift": "5s",
	  "frozen_height": {
		"revision_number": "0",
		"revision_height": "0"
	  },
	  "latest_height": {
		"revision_number": "1",
		"revision_height": "8"
	  },
	  "proof_specs": [
		{
		  "leaf_spec": {
			"hash": "SHA256",
			"prehash_key": "NO_HASH",
			"prehash_value": "SHA256",
			"length": "VAR_PROTO",
			"prefix": "AA=="
		  },
		  "inner_spec": {
			"child_order": [
			  0,
			  1
			],
			"child_size": 33,
			"min_prefix_length": 4,
			"max_prefix_length": 12,
			"empty_child": null,
			"hash": "SHA256"
		  },
		  "max_depth": 0,
		  "min_depth": 0
		},
		{
		  "leaf_spec": {
			"hash": "SHA256",
			"prehash_key": "NO_HASH",
			"prehash_value": "SHA256",
			"length": "VAR_PROTO",
			"prefix": "AA=="
		  },
		  "inner_spec": {
			"child_order": [
			  0,
			  1
			],
			"child_size": 32,
			"min_prefix_length": 1,
			"max_prefix_length": 1,
			"empty_child": null,
			"hash": "SHA256"
		  },
		  "max_depth": 0,
		  "min_depth": 0
		}
	  ],
	  "upgrade_path": [
		"upgrade",
		"upgradedIBCState"
	  ],
	  "allow_update_after_expiry": false,
	  "allow_update_after_misbehaviour": false
	},
	"proof_height": {
	  "revision_number": "2",
	  "revision_height": "11"
	},
	"proof_try": "",
	"proof_client": "",
	"proof_consensus": "",
	"consensus_height": {
	  "revision_number": "1",
	  "revision_height": "8"
	},
	"signer": "cro10snhlvkpuc4xhq82uyg5ex2eezmmf5ed5tmqsv"
}

`, &anyRawValue)
			must.Do(decoder.Decode(anyRawValue))

			anyParams := ibc_model.MsgConnectionOpenAckParams{
				MsgConnectionOpenAckBaseParams: anyRawMsgConnectionOpenAck.MsgConnectionOpenAckBaseParams,
				MaybeTendermintClientState:     &anyRawMsgConnectionOpenAck.TendermintClientState,

				ClientID:             anyClientId,
				CounterpartyClientID: anyCounterpartyClientId,
			}

			event := event_usecase.NewMsgIBCConnectionOpenAck(event_usecase.MsgCommonParams{
				BlockHeight: anyHeight,
				TxHash:      anyTxHash,
				TxSuccess:   false,
				MsgIndex:    anyMsgIndex,
			}, anyParams)

			encoded, err := event.ToJSON()
			Expect(err).To(BeNil())

			decodedEvent, err := registry.DecodeByType(
				event_usecase.MSG_IBC_CONNECTION_OPEN_ACK_FAILED, 1, []byte(encoded),
			)
			Expect(err).To(BeNil())
			Expect(decodedEvent).To(Equal(event))
			typedEvent, _ := decodedEvent.(*event_usecase.MsgIBCConnectionOpenAck)
			Expect(typedEvent.Name()).To(Equal(event_usecase.MSG_IBC_CONNECTION_OPEN_ACK_FAILED))
			Expect(typedEvent.Version()).To(Equal(1))
			Expect(typedEvent.TxSuccess()).To(BeFalse())
			Expect(typedEvent.TxHash()).To(Equal(anyTxHash))

			Expect(typedEvent.MsgTxHash).To(Equal(anyTxHash))
			Expect(typedEvent.MsgIndex).To(Equal(anyMsgIndex))

			Expect(typedEvent.Params.ConnectionID).To(Equal(anyParams.ConnectionID))
			Expect(typedEvent.Params.CounterpartyConnectionID).To(Equal(anyParams.CounterpartyConnectionID))
			Expect(typedEvent.Params.Version).To(Equal(anyParams.Version))
			Expect(typedEvent.Params.ProofHeight).To(Equal(anyParams.ProofHeight))
			Expect(typedEvent.Params.ProofTry).To(Equal(anyParams.ProofTry))
			Expect(typedEvent.Params.ProofClient).To(Equal(anyParams.ProofClient))
			Expect(typedEvent.Params.ProofConsensus).To(Equal(anyParams.ProofConsensus))
			Expect(typedEvent.Params.ConsensusHeight).To(Equal(anyParams.ConsensusHeight))
			Expect(typedEvent.Params.Signer).To(Equal(anyParams.Signer))

			Expect(typedEvent.Params.MaybeTendermintClientState).To(Equal(anyParams.MaybeTendermintClientState))

			Expect(typedEvent.Params.ClientID).To(Equal(anyParams.ClientID))
			Expect(typedEvent.Params.CounterpartyClientID).To(Equal(anyParams.CounterpartyClientID))
		})
	})
})
