package parser_test

import (
	"regexp"
	"strings"

	"github.com/crypto-com/chain-indexing/external/json"
	"github.com/crypto-com/chain-indexing/usecase/parser/utils"

	. "github.com/onsi/ginkgo"
	. "github.com/onsi/gomega"

	"github.com/crypto-com/chain-indexing/infrastructure/tendermint"
	"github.com/crypto-com/chain-indexing/usecase/event"
	"github.com/crypto-com/chain-indexing/usecase/parser"
	usecase_parser_test "github.com/crypto-com/chain-indexing/usecase/parser/test"
)

var _ = Describe("ParseMsgCommands", func() {
	Describe("MsgIBCChannelOpenInit", func() {
		It("should parse Msg commands when there is MsgChannelOpenInit in the transaction", func() {
			expected := `{
  "name": "MsgChannelOpenTryCreated",
  "version": 1,
  "height": 16,
  "uuid": "{UUID}",
  "msgName": "MsgChannelOpenTry",
  "txHash": "CB12276476D61977AB1625CB5BBA9C1890EF3D9592DB7A9A5C5EC32D941A7B81",
  "msgIndex": 1,
  "params": {
	"portId": "transfer",
	"previousChannelId": "",
	"channel": {
	  "state": "STATE_TRYOPEN",
	  "ordering": "ORDER_UNORDERED",
	  "counterparty": {
		"portId": "transfer",
		"channelId": "channel-0"
	  },
	  "connectionHops": [
		"connection-0"
	  ],
	  "version": "ics20-1"
	},
	"counterpartyVersion": "ics20-1",
	"proofInit": "",
	"proofHeight": {
	  "revisionNumber": "1",
	  "revisionHeight": "15"
	},
	"signer": "cro1dulwqgcdpemn8c34sjd92fxepz5p0sqpeevw7f",

    "channelId": "channel-0",
    "connectionId": "connection-0"
  }
}`

			txDecoder := utils.NewTxDecoder()
			block, _, _ := tendermint.ParseBlockResp(strings.NewReader(
				usecase_parser_test.TX_MSG_CHANNEL_OPEN_TRY_BLOCK_RESP,
			))
			blockResults, _ := tendermint.ParseBlockResultsResp(strings.NewReader(
				usecase_parser_test.TX_MSG_CHANNEL_OPEN_TRY_BLOCK_RESULTS_RESP,
			))

			accountAddressPrefix := "cro"
			stakingDenom := "basecro"

			pm := usecase_parser_test.InitParserManager()

			cmds, err := parser.ParseBlockTxsMsgToCommands(
				pm,
				txDecoder,
				block,
				blockResults,
				accountAddressPrefix,
				stakingDenom,
			)
			Expect(err).To(BeNil())
			Expect(cmds).To(HaveLen(2))
			cmd := cmds[1]
			Expect(cmd.Name()).To(Equal("CreateMsgIBCChannelOpenTry"))

			untypedEvent, _ := cmd.Exec()
			typedEvent := untypedEvent.(*event.MsgIBCChannelOpenTry)

			regex, _ := regexp.Compile("\n?\r?\\s?")

			Expect(json.MustMarshalToString(typedEvent)).To(Equal(
				strings.Replace(
					regex.ReplaceAllString(expected, ""),
					"{UUID}",
					typedEvent.UUID(),
					-1,
				),
			))
		})
	})
})
